import {
	appendChildToContainer,
	commitUpdate,
	Container,
	removeChild
} from 'hostConfig';
import { FiberNode, FiberRootNode } from './fiber';
import {
	mutationMask,
	noFLags,
	placement,
	update,
	childDeletion
} from './fiberFlags';
import {
	FunctionComponent,
	HostComponent,
	HostRoot,
	HostText
} from './workTag';

let nextEffect: FiberNode | null = null;
export function commitMutationEffects(finishedWork: FiberNode) {
	nextEffect = finishedWork;
	while (nextEffect !== null) {
		const child: FiberNode | null = nextEffect.child;
		if (
			(nextEffect.subtreeFlags & mutationMask) !== noFLags &&
			child !== null
		) {
			nextEffect = child;
		} else {
			up: while (nextEffect !== null) {
				commitMutationEffectsOnFiber(nextEffect);
				const sibling: FiberNode | null = nextEffect.sibling;
				if (sibling !== null) {
					nextEffect = sibling;
					break up;
				}
				nextEffect = nextEffect.return;
			}
		}
	}
}

const commitMutationEffectsOnFiber = (fiber: FiberNode) => {
	const { flags } = fiber;
	if ((flags & placement) !== noFLags) {
		commitPlacement(fiber);
		fiber.flags &= ~placement;
	}
	if ((flags & update) !== noFLags) {
		commitUpdate(fiber);
		fiber.flags &= ~update;
	}
	if ((flags & childDeletion) !== noFLags) {
		const deletions = fiber.deletions;
		if (deletions !== null) {
			deletions.forEach((childToDelete) => {
				commitDeletion(childToDelete);
			});
		}
		fiber.flags &= ~childDeletion;
	}
};

const commitPlacement = (fiber: FiberNode) => {
	if (__DEV__) {
		console.warn('commitPlacement', fiber);
	}
	const parent = getHostParent(fiber);
	appendPlacementNodeToContainer(fiber, parent);
};

const commitDeletion = (childToDelete: FiberNode) => {
	let rootHostNode: FiberNode | null = null;
	commitNestedComponent(childToDelete, (unmountFiber) => {
		switch (unmountFiber.tag) {
			case HostComponent:
				if (rootHostNode === null) {
					rootHostNode = unmountFiber;
				}
				return;
			case HostText:
				if (rootHostNode === null) {
					rootHostNode = unmountFiber;
				}
				return;
			case FunctionComponent:
				return;
			default:
				if (__DEV__) {
					console.warn('未处理的unmount类型', unmountFiber);
				}
				break;
		}
	});
	if (rootHostNode !== null) {
		const hostParent = getHostParent(childToDelete);
		removeChild((rootHostNode as FiberNode).stateNode, hostParent);
	}
	childToDelete.return = null;
	childToDelete.child = null;
};

function commitNestedComponent(
	root: FiberNode,
	onCommitUnmount: (fiber: FiberNode) => void
) {
	let node = root;

	while (true) {
		onCommitUnmount(node);

		if (node.child !== null) {
			node.child.return = node;
			node = node.child;
			continue;
		}

		if (node === root) {
			return;
		}

		while (node.sibling === null) {
			if (node.return === null || node.return === root) {
				return;
			}

			node = node.return;
		}

		node.sibling.return = node.return;
		node = node.sibling;
	}
}
function getHostParent(fiber: FiberNode): Container {
	let parent = fiber.return;
	let result: Container | null = null;
	while (parent !== null) {
		const parentTag = parent.tag;
		if (parentTag === HostComponent) {
			result = parent.stateNode;
			break;
		}
		if (parentTag === HostRoot) {
			result = (parent.stateNode as FiberRootNode).container;
			break;
		}
		parent = parent.return;
	}
	if (__DEV__ && result === null) {
		console.warn('getHostParent not found', fiber);
	}
	return result!;
}

function appendPlacementNodeToContainer(
	fiber: FiberNode,
	hostParent: Container
) {
	if (fiber.tag === HostComponent || fiber.tag === HostText) {
		appendChildToContainer(hostParent, fiber.stateNode);
		return;
	}
	const child = fiber.child;
	if (child !== null) {
		appendPlacementNodeToContainer(child, hostParent);
		let sibling = child.sibling;
		while (sibling !== null) {
			appendPlacementNodeToContainer(sibling, hostParent);
			sibling = sibling.sibling;
		}
	}
}
